sys_variable_value.value:
(function execute(inputs, outputs) {

	inputs.grrecomanacio.objectius=new Reco().getNObjectiusSysId(inputs.grrecomanacio.sys_id);
	inputs.grrecomanacio=new Reco().getEstat(inputs.grrecomanacio);
	if (inputs.grrecomanacio.estat!=5) inputs.grrecomanacio.tancament=""; //per si estava tancada però s'han obert objectius nous
	inputs.grrecomanacio.update();

})(inputs, outputs);

sys_hub_action_input.calculation:
(function calculatedFieldValue(current) {

	// Add your code here
	return '';  // return the calculated value

})(current);

