sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	if (isLoading) {
		return;
	} else {

		if (newValue > 100 || newValue <0) {
			g_form.setValue('percentatge',oldValue);
		}

		if (newValue!=100 ) {
			g_form.clearValue('justificaciopercentatge');
			g_form.setMandatory('justificaciopercentatge',true);
			g_form.showFieldMsg('justificaciopercentatge',"Cal una nova justificació del grau d\'assoliment");

		}

		if (newValue == 100) {
			g_form.setMandatory('justificaciopercentatge',false);
			if (g_form.getValue('justificaciopercentatge')=="") {
				g_form.clearValue('justificaciopercentatge');
				g_form.showFieldMsg('justificaciopercentatge',"Es recomana una justificació de l\'assoliment");
			}
		}
	}
}
