function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    /*if (isLoading || newValue === '') {
    	return;
    }
    */

    if (g_form.getValue('state') == -5) {
        var t = g_form.getReference('tipus', setNom);
    }
}

function setNom(tipuscanvitit) {
    if (tipuscanvitit.mascara && g_form.getValue('state') == -5) {
        var titulacio = '';
        var param1 = '';
        if (g_form.getDisplayBox('titulacio').value) titulacio = g_form.getDisplayBox('titulacio').value;
        if (g_form.getValue('param1')) param1 = g_form.getValue('param1');
        g_form.setValue('short_description',
            tipuscanvitit.mascara.replace("$titulaciÃ³", titulacio).replace("$param1", param1));
    } else g_form.setValue('short_description', '');
}
