sys_script_fix.script:
new log("Inici execució script reducció rols");
var baixa_rols = true; //Si està false l'escript corre, però no baixa els rols realment
var gcdt = new GlideDateTime();
var mesos_darrera = gs.getProperty('x_udg_q.mesos_endarrera_canvi_rol');
if (!mesos_darrera || mesos_darrera < 1) mesos_darrera = 12;
gcdt.addMonthsLocalTime(-mesos_darrera);
var datalimit = gcdt.getDate().toString();
new log("Data límit reducció rols: " + datalimit);
var grF = new GlideRecord('x_udg_q_funcio');
grF.setWorkflow(false); // per no executar la BR after
grF.addQuery('readonly', false); //només funcions que no siguin de lectura
grF.query();
var funcionstotal = 0;
var funcionsnolectura = 0;
var personesafectades = 0;
while (grF.next()) {
    funcionstotal++;
    var arr = grF.membres.split(",");
    var usuarisrecents = 0;
    funcionsnolectura++;
    for (var i = 0; i < arr.length; i++) {
        var grU = new GlideRecord('sys_user');
        grU.get(arr[i]);
        var U = new x_udg_q.Usuari(grU.sys_id);
        var datlogin = grU.last_login_time.toString().split(' ');
        if (datlogin[0] < datalimit && !U.hasRol('x_udg_q.Manager') && U.hasRol('x_udg_q.User')) { //Last login anterior a la data límit i te els rols
            personesafectades++;
            new log("Reduint rol a " + grF.nom + " - " + grF.sgq.codi + " - " + grU.name + " - " + datlogin[0]);
            if (baixa_rols) {
                U.setRol('x_udg_q.Observador');
                U.delRol('x_udg_q.User');
            }
        } else {
            //new log("Skipping rol a " + grF.nom + " - " + grF.sgq.codi + " - " + grU.name + " - " + datlogin[0]);
        }
    }
}
new log("Total funcions revisades: " + funcionstotal);
new log("Funcions no lectura: " + funcionsnolectura);
new log("Total persones afectardes: " + personesafectades);
new log("Inici execució script reducció rols");
