sys_script_fix.script:
new x_udg_q.log("Script reduir rols - Inici execució");
var baixa_rol = true;
var ara= new GlideDateTime();
var gcdt = new GlideDateTime();
gcdt.addMonthsLocalTime(-(gs.getProperty('x_udg_q.mesos_endarrera_canvi_rol')));
var datalimit = gcdt.getDate().toString();
new x_udg_q.log("Script reduir rols - Data límit reducció rols: " + datalimit);
var grF = new GlideRecord('x_udg_q_funcio');
grF.setWorkflow(false); // per no executar la BR after
grF.query();
var funcionstotal = 0;
var funcionsnolectura = 0;
var personesafectades = 0;
while (grF.next()) {
    funcionstotal++;
    var arr = grF.membres.split(",");
    var usuarisrecents = 0;
    if (!grF.readonly) { //només funcions que no siguin de lectura
        funcionsnolectura++;
        for (var i = 0; i < arr.length; i++) {
            var grU = new GlideRecord('sys_user');
            grU.get(arr[i]);
            var U = new x_udg_q.Usuari(grU.sys_id);
            var datlogin = grU.last_login_time.toString().split(' ');
            if (datlogin[0] < datalimit && !U.hasRol('x_udg_q.Manager') && U.hasRol('x_udg_q.User')) { //Anterior a la data limit i te els rols
                personesafectades++;
                new x_udg_q.log ("Script reduir rols - Reduint rol a " + grF.nom + " - " + grF.sgq.codi + " - " + grU.name + " - " + datlogin[0]);
                if (baixa_rol) {
                    U.setRol('x_udg_q.Observador');
                    U.delRol('x_udg_q.User');
                    grU.x_udg_q_degradat = ara.getDisplayValue().toString();
                    grU.update();
                }
            }
        }
    }
}
new x_udg_q.log("Script reduir rols - Total funcions revisades: " + funcionstotal);
new x_udg_q.log("Script reduir rols - Funcions no lectura: " + funcionsnolectura);
new x_udg_q.log("Script reduir rols - Total usuaris reduits: " + personesafectades);
new x_udg_q.log("Script reduir rols - Fi execució");
