sys_script_include.script:
var FuncioUtil = Class.create();
FuncioUtil.prototype = {
	initialize: function() {
	},

	registraHistoric: function (current, previous) {
		var gr = new GlideRecord("x_udg_q_historicfuncio");
		var ha_canviat =false;
		gr.data= new GlideDateTime().getDisplayValue();
		gr.funcio=previous.sys_id;

		gr.activaN=current.activa;

		if (previous.activa != current.activa) {
			ha_canviat=true;
		}

		if (previous.nom != current.nom) {
			ha_canviat=true;
			gr.nomv=previous.nom;
			gr.nomn=current.nom;
		}

		if (previous.usuari != current.usuari) {
			ha_canviat=true;
			gr.usuariv=previous.usuari;
			gr.usuarin=current.usuari;
		}

		if (previous.tipus != current.tipus) {
			ha_canviat=true;
			gr.tipusv=previous.tipus;
			gr.tipusn=current.tipus;
		}

		if (previous.suport != current.suport) {
			ha_canviat=true;
			gr.suportv=previous.suport;
			gr.suportn=current.suport;
		}

		if (previous.membres_funcions != current.membres_funcions) {
			ha_canviat=true;
			gr.membres_funcionsv=previous.membres_funcions;
			gr.membres_funcionsn=current.membres_funcions;
		}

		if (previous.membres_titol_personal!= current.membres_titol_personal) {
			ha_canviat=true;
			gr.membres_titol_personalv=previous.membres_titol_personal;
			gr.membres_titol_personaln=current.membres_titol_personal;
		}


		// Assegurar que s'ha actuaalitzat la llista per no generar dos registres
		if (previous.membres != current.membres) {
			ha_canviat=true;
			gr.membresv=previous.membres;
			gr.membresv=current.membres;
		}

		if (ha_canviat)
			gr.insert();
	},

	type: 'FuncioUtil'
};
