sys_script_include.script:
var Funcio = Class.create();
Funcio.prototype = {
    initialize: function(grFuncio) { //funcio Gliderecord
        this._funcio = grFuncio;
    },

    //actualitza tots els assignats malament de totes les funcions
    actualitzar_tots_assignats: function() {
        var gr1 = new GlideRecord("x_udg_q_objectiumillora");
        gr1.addQuery('state','2');
        gr1.query();
        while (gr1.next()) {
            if (gr1.responsable.membres.toString().indexOf(gr1.assigned_to.toString()) == -1 || gr1.assigned_to=="") {
                new log("Reassingnant " + gr1.number + " de " + gr1.assigned_to.name + " a " + gr1.responsable.usuari.name + " - Funcio.actualitzar_tots_assignats()");
                gr1.assigned_to = gr1.responsable.usuari;
                gr1.update();
            }
        }
        var gr2 = new GlideRecord("x_udg_q_acciomillora");
        gr2.addQuery('state','2');
        gr2.query();
        while (gr2.next()) {
            if (gr2.responsable.membres.toString().indexOf(gr2.assigned_to.toString()) == -1 || gr2.assigned_to=="") {
                new log("Reassingnant " + gr2.number + " de " + gr2.assigned_to.name + " a " + gr2.responsable.usuari.name + " - Funcio.actualitzar_tots_assignats()");
                gr2.assigned_to = gr2.responsable.usuari;
                gr2.update();
            }
        }   
    },

    type: 'Funcio'
};
