sys_script_include.script:
var Funcio = Class.create();
Funcio.prototype = {
	initialize: function (grFuncio) { //funcio Gliderecord
		this._funcio = grFuncio;
	},

	actualitzar_tots_assignats: function () {
		var gr1 = new GlideRecord("x_udg_q_objectiumillora");
		gr1.addQuery ('responsable',this._funcio.sys_id);
		//new log( "Buscant "+this._funcio.sys_id);
		gr1.query();
		while (gr1.next()) {
			gr1.assigned_to = this._funcio.usuari;
			gr1.update();
		}
		var gr2 = new GlideRecord("x_udg_q_acciomillora");
		gr2.addQuery ('responsable',this._funcio.sys_id);
		//new log( "Buscant "+this._funcio.sys_id);
		gr2.query();
		while (gr2.next()) {
			gr2.assigned_to = this._funcio.usuari;
			gr2.update();
		}
	},


	type: 'Funcio'
};
