sys_script_include.script:
var AccioUtils = Class.create();
AccioUtils.prototype = {
	initialize: function(accio) {
		this._accio=accio;
	},

	actualitzaAssolimentTotsObjectius: function (accio_id) {
		//Actualitza tots els objectius als que contribueix una acciÃ³
		new x_udg_q.log("AccioUtils.actualitzaAssolimentTotsObjectius: "+accio_id);
		var grM2m = new GlideRecord ('x_udg_q_m2m_accions_objectius');
		grM2m.addQuery('accio_millora',accio_id);
		grM2m.query();
		while (grM2m.next()) {
			new x_udg_q.log("AccioUtils.actualitzaAssolimentTotsObjectius crida ObjectiuUtils.actualitzaAssoliment per : "+grM2m.objectiu_millora.number);
			new ObjectiuUtils().actualitzaAssoliment(grM2m.objectiu_millora);
		}
	},

	setAllResponsables: function (responsabilitat) {
		var grAcc = new GlideRecord ('x_udg_q_acciomillora');
		grAcc.addQuery('responsable', responsabilitat.sys_id);
		grAcc.addQuery('state!=-5'); //No esborrany
		grAcc.query();
		gs.info("SC - "+responsabilitat.sgq.codi+" AccioUtils.actualitzaAccioResponsable busca accions amb responsable: "+ responsabilitat.nom);
		while (grAcc.next()) {
			gs.info("SC - "+grAcc.sgq.codi+" - " +grAcc.number +" AccioUtils.actualitzaAccionsResponsable actualitza ("+responsabilitat.nom +") " + grAcc.assigned_to.name +" -> "+ responsabilitat.usuari.name);
			grAcc.assigned_to=responsabilitat.usuari;
			grAcc.update();
		}
	},

	calculaEstat: function (current) {
		if (current.percentatge <= 0)
			return 1;
		if (current.percentatge >= 100)
			return 3;
		return 2;	
	},

	type: 'AccioUtils'
};
