sys_script_include.script:
var CAI = Class.create();
CAI.prototype = {
    initialize: function(sysidCAI) {
		if (sysidCAI) this._sysidCAI = sysidCAI;
	},

    llistaSysidsTitulacionsAcreditacions: function(llistaSysidsAcreditacions) {
        var titulacions = [];
        var grAcreditacio = new GlideRecord('x_udg_q_acreditacio');
        grAcreditacio.addQuery('sys_id', 'IN', llistaSysidsAcreditacions);
        grAcreditacio.query();
        while (grAcreditacio.next()) {
            titulacions.push("" + grAcreditacio.titulacio);
        }
        return titulacions.join();
    },

    type: 'CAI'
};
