sys_script_include.script:
var Reco = Class.create();
Reco.prototype = {
	initialize: function() {
	},

	getEstat: function(recomanacio) {
		if (recomanacio.tancament!="")
			return 5; //Tancada
		else {
			var grObjFills = new GlideRecord('x_udg_q_objectiumillora');
			grObjFills.addEncodedQuery("state!=-5^recomanacionsLIKE"+recomanacio.sys_id);
			grObjFills.query();
			if (!grObjFills.hasNext()) { //No hi ha objectius ni en curs ni tancats
				if (recomanacio.valoracio == "")
					return 1;  //Nova
				else
					return 2; //Te valoraciÃ³
			}
			else { //Hi ha objectius en curs o tancats
				return 3; //objectius en curs	
			}
		}
	},

	actualitzaEstatLlista: function (recomanacions) {
		gs.addInfoMessage('Cridat actualitzaEstatLlista per '+recomanacions);
		var grR = new GlideRecord('x_udg_q_recomanacions');
		grR.addEncodedQuery("sys_idIN"+recomanacions);
		grR.query();
		while (grR.next()) {
			var nouEstat=this.getEstat(grR);
			gs.addInfoMessage('actualitzaEstatLlista per '+grR.sys_id+ " - "+grR.number + " Nou estat "+nouEstat);
			if (nouEstat != grR.estat) {
				gs.addInfoMessage("Actualitzo");
				grR.estat=nouEstat;
				grR.update();
			}
		}
	},

	type: 'Reco'
};
