sys_script_include.script:
var Reco = Class.create();
Reco.prototype = {
	initialize: function() {
	},

	getEstat: function(recomanacio) {
		var grObjFills = new GlideRecord('x_udg_q_objectiumillora');
		var query="state!=-5^recomanacionsLIKE"+recomanacio.sys_id;
		grObjFills.addEncodedQuery(query);
		grObjFills.query();
		if (!grObjFills.hasNext()) { //No hi ha objectius ni en curs ni tancats
			return 1;  //nova
		} else { //Hi ha objectius en curs o tancats
			var grObjFills2 = new GlideRecord('x_udg_q_objectiumillora');
			var query1="recomanacionsLIKE"+recomanacio.sys_id;
			var query2="state=2"; //Algun és en curs?
			grObjFills2.addEncodedQuery(query1);
			grObjFills2.addEncodedQuery(query2);
			grObjFills2.query();
			if (grObjFills2.hasNext()) { //algun és en curs
				return 2; //objectius en curs
			} else { //cap és en curs
				return 3;  //tancada
			}
		}
	},

	type: 'Reco'
};
