sys_script_include.script:
var Usuari = Class.create();
Usuari.prototype = {
	initialize: function(usuari) {
		this.usuari = usuari; //sys_id d'un usuari
	},

	addSGQ: function(sgq_id) {
		var gr = new GlideRecord("sys_user");
		gr.addQuery('sys_id', this.usuari);
		gr.query();
		if (gr.next()) {
			var l = new ListUtil(gr.x_udg_q_sgq_visibles);
			if (! l.isInList(sgq_id)) { //no esta inclós, encara
				l.addToList(sgq_id);
				if (gr.x_udg_q_sgq_actual == '' )
					gr.x_udg_q_sgq_actual=sgq_id;
				gr.x_udg_q_sgq_visibles=l.getList();
				gr.update();
			}
		}
	},

	addRole: function (role_name) { //Role is the role name
		var gr = new GlideRecord('sys_user_has_role');
		gr.addQuery('user',this.usuari);
		gr.addQuery('role',role_name);
		gr.query();
		if(gr.next()) {
			new log("Ja té el rol");
		} else {
			gr.user=this.usuari;
			gr.role= this._getRoleIdByName(role_name);
			if (gr.role !="" && gr.user!="")
				gr.insert();
			else
				new log ("Error insert addRole");
		}
	},

	_getRoleIdByName: function(role){
		var gr = new GlideRecord("sys_user_role");
		gr.addQuery("name", role);
		gr.query();
		if (gr.next())
			return gr.sys_id;
		else
			return "";
	},
	type: 'Usuari'
};
