sys_script_include.script:
var Usuari = Class.create();
Usuari.prototype = {
	initialize: function(usuari) {
		this.usuari = usuari; //sys_id d'un usuari
	},

	addSGQ: function(sgq_id) {
		var gr = new GlideRecord("sys_user");
		gr.addQuery('sys_id', this.usuari);
		gr.query();
		if (gr.next()) {
			var l = new ListUtil(gr.x_udg_q_sgq_visibles);
			if (! l.isInList(sgq_id)) { //no esta inclós, encara
				l.addToList(sgq_id);
				if (gr.x_udg_q_sgq_actual == '' )
					gr.x_udg_q_sgq_actual=sgq_id;
				gr.x_udg_q_sgq_visibles=l.getList();
				gr.update();
			}
		}
	},

	_getSysIdRol: function (rol) {
		var grManagerUniversitat = new GlideRecord('sys_user_role');
		grManagerUniversitat.get('name',rol);
		return grManagerUniversitat.sys_id.toString();
	},


	addRole: function (role_name) { //Role is the role name
		var gr = new GlideRecord('sys_user_has_role');
		gr.addQuery('user',this.usuari);
		gr.addQuery('role',role_name);
		gr.query();
		if(gr.next()) {
			// new log("Ja té el rol");
		} else {
			gr.user=this.usuari;
			gr.role= this._getRoleIdByName(role_name);
			if (gr.role !="" && gr.user!="")
				gr.insert();
			else
				new log ("Error insert x_udg_q.Usuari.addRole() "+this.usuari);
		}
	},

	_getRoleIdByName: function(role){
		var gr = new GlideRecord("sys_user_role");
		gr.addQuery("name", role);
		gr.query();
		if (gr.next())
			return gr.sys_id;
		else
			return "";
	},

	isManagerUniversitat: function(){
		var gr = new GlideRecord("x_udg_q_gestorsuniversitat");
		gr.addQuery('actiu',true);
		gr.addQuery('usuari',this.usuari);
		gr.setLimit(1);
		gr.query();
		var itIs = gr.next();
		return itIs;
	},

	isObservador: function (sgq) {
		var gr = new GlideRecord("x_udg_q_observador");
		if (sgq)
			gr.addQuery('sgq',sgq);
		gr.addQuery('actiu',true);
		gr.addQuery('usuari',this.usuari);
		gr.setLimit(1);
		gr.query();
		var itIs = gr.next();
		//gs.info("Observador ? "+this.usuari+" "+itIs+" sgq: "+sgq.codi);
		return itIs;
	},

	isManager: function (sgq) {
		var gr = new GlideRecord("x_udg_q_sgq");
		if (sgq) gr.addQuery('sys_id',sgq);
			gr.addQuery('actiu',true);
		var qc = gr.addQuery('responsable.usuari', this.usuari);
		qc.addOrCondition('responsable.suport','CONTAINS',this.usuari);
		qc.addOrCondition('propietari.usuari', this.usuari);
		qc.addOrCondition('propietari.suport','CONTAINS',this.usuari);
		gr.setLimit(1);
		gr.query();
		var itIs = gr.next();
		return itIs;
	},

	isUser: function (sgq) {
		var gr = new GlideRecord("x_udg_q_funcio");
		if (sgq) gr.addQuery('sgq',sgq);
			gr.addQuery('activa',true);
		var qc = gr.addQuery('usuari',this.usuari);
		qc.addOrCondition('suport', 'CONTAINS',this.usuari);
		gr.setLimit(1);
		gr.query();
		var itIs = gr.next();
		return itIs;
	},



	sgqVisibles: function () {
		var llistaSgq = [];
		var grSgq = new GlideRecord('x_udg_q_sgq');
		grSgq.addQuery('actiu',true);
		grSgq.query();
		while (grSgq.next()) {
			if (this.isManagerUniversitat() || this.isManager(grSgq.sys_id)|| this.isUser(grSgq.sys_id) || this.isObservador(grSgq.sys_id)) {
				//new log("Afegir: "+grSgq.sys_id.toString());
				llistaSgq.push(grSgq.sys_id.toString());
				//new log("Ara llistaSgq és: "+llistaSgq);
			}
		}
		//new log("Definitivament la llista toString és: "+llistaSgq.toString());
		return llistaSgq.toString();
	},

	setRoles: function (sgq) {
		new log("setRoles per "+this.usuari);

		//ManagerUniversitat
		var idMU= this._getSysIdRol('x_udg_q.ManagerUniversitat');
		var gr1 = new GlideRecord('sys_user_has_role');
		gr1.addQuery('user', this.usuari);
		gr1.addQuery('role', idMU);
		gr1.query();
		var foundMU=gr1.next();
		var isMU= this.isManagerUniversitat();
		if (isMU && !foundMU) {
			gr1.initialize();
			gr1.user=this.usuari;
			gr1.role=idMU;
			new log("Afegim ManagerUniversitat per "+gr1.user);
			gr1.insert();
		}
		if (!isMU && foundMU) {
			new log("Esborrem ManagerUniversitat per "+gr1.user);
			gr1.deleteRecord();
		}
		//Manager
		var idManager = this._getSysIdRol('x_udg_q.Manager');
		var gr2 = new GlideRecord('sys_user_has_role');
		gr2.addQuery('user', this.usuari);
		gr2.addQuery('role', idManager);
		gr2.addQuery('inherited', false);
		gr2.query();
		var foundManager=gr2.next();
		var isManager= this.isManager(sgq);
		if (isManager && !foundManager) {
			gr2.initialize();
			gr2.user=this.usuari;
			gr2.role=idManager;
			new log("Afegim manager per "+gr2.user);
			gr2.insert();
		}
		if (!isManager && foundManager) {
			new log("Esborrem manager per "+gr2.user);
			gr2.deleteRecord();
		}

		//User
		var idUser = this._getSysIdRol('x_udg_q.User');
		var gr3 = new GlideRecord('sys_user_has_role');
		gr3.addQuery('user', this.usuari);
		gr3.addQuery('role', idUser);
		gr3.addQuery('inherited', false);
		gr3.query();
		var foundUser=gr3.next();
		var isUser= this.isUser(sgq);
		if (isUser && !foundUser) {
			gr3.initialize();
			gr3.user=this.usuari;
			gr3.role=idUser;
			new log("Afegim user per "+gr3.user);
			gr3.insert();
		}
		if (!isUser && foundUser) {
			new log("Esborrem user per "+gr3.user);
			gr3.deleteRecord();
		}

		//Observador
		var idOb = this._getSysIdRol('x_udg_q.Observador');
		var gr4 = new GlideRecord('sys_user_has_role');
		gr4.addQuery('user', this.usuari);
		gr4.addQuery('role', idOb);
		gr4.addQuery('inherited', false);
		gr4.query();
		var foundOb=gr4.next();
		var isOb= this.isObservador(sgq);
		if (isOb && !foundOb) {
			gr4.initialize();
			gr4.user=this.usuari;
			gr4.role=idOb;
			new log("Afegim Observador per "+gr4.user);
			gr4.insert();
		}
		if (!isOb && foundOb) {
			new log("Esborrem Observador per "+gr4.user);
			gr4.deleteRecord();
		}
	},

	type: 'Usuari'
};
