sys_ui_action.script:
// Client side onclick function
function onClick(){

	var mandatoryFields = ['state'];
	var state=g_form.getValue('state');

	mandatoryFields.push('responsable');
	mandatoryFields.push('priority');
	//mandatoryFields.push('due_date');
	mandatoryFields.push('short_description');

	var errorMessage = g_form.checkMandatoryFields(mandatoryFields);

	if (!errorMessage){
		gsftSubmit(null, g_form.getFormElement(), 'Declara Objectiu'); //MUST call the 'Action name' set in this UI Action
	}else{
		g_form.clearMessages();
		g_form.addErrorMessage("Manquen camps obligatoris: "+ errorMessage );
	}
}


// Code that runs without 'onclick'
// Ensure call to server side function with no browser errors
if (typeof window == 'undefined')
	doDefinirObjectiu();

function doDefinirObjectiu() {

	if (current.responsable == '') { //Check de tots els camps obligatoris
		current.update(); //gravem igualment
		gs.addErrorMessage(current.number+" s'ha desat com esborrany perÃ² NO ES POT DECLARAR per <B>manca de responsable</B>");
	}
	else {
		var o= new ObjectiuUtils();
		current.state = 2;
		current.active=true;
		current.accions=o.getAccions(current.sys_id);
		current.assoliment=o.getAssoliment(current.sys_id);
		var ara = new GlideDateTime();
		current.declarat= ara.toString();
		current.declarat_per=gs.getUserID();
		current.closed_by="";
		current.closed_at="";
		current.update();
		gs.addInfoMessage(current.number+ " Declarat");
	}
	action.setRedirectURL(current);  //Roman (Si no, no es veuen els camps obligatoris)
}
