sys_ui_action.script:
var SGQpref=gs.getUser().getPreference('qualitat.sgqActual');
var SGQprefCodi=gs.getUser().getPreference('qualitat.sgqActualCodi');
var grDG = new GlideRecord ('x_udg_q_docgeneratvsma');
grDG.initialize();
grDG.insert();

var grPlantilla= new GlideRecord('sys_attachment');
grPlantilla.get(current.file); //GlideRecord del fitxer
var gsaPlantilla = new GlideSysAttachment();
var contingut=gsaPlantilla.getContent(grPlantilla);


var rtf1= new rtf(contingut);
rtf1.replace (/##NomSGQ##/g,current.sgq.nom);
rtf1.replace (/##LlistaTitulacions##/g,rtf1.llista(new Sgq().getLlistaNomsTitulacionsSgqActual()));


var subestandards= rtf1.getContent().match(/##TSubestandard#.\..##/g).toString().match(/.\../g);
for (index=0; index<subestandards.length; ++index) {
	var sysidS = new EstandardVSMA().getSysidSubestandardNum(subestandards[index]);
	var nom= new EstandardVSMA().getNomSubestandard(sysidS);
	rtf1.replace("##TSubestandard#"+subestandards[index]+"##",nom);
}

var Dsubestandards= rtf1.getContent().match(/##DSubestandard#.\..##/g).toString().match(/.\../g);
for (index=0; index<Dsubestandards.length; ++index) {
	var sysidS = new EstandardVSMA().getSysidSubestandardNum(Dsubestandards[index]);
	var Obj= new ObjectiuUtils();
	//gs.addInfoMessage("Trobat Dsubstandard "+Dsubestandards[index]+" sysISD "+sysidS);
	var textstring= Obj.getRtfperSubestandard(sysidS);
	//gs.addInfoMessage("Textstring tornat: "+textstring);
	rtf1.replace("##DSubestandard#"+Dsubestandards[index]+"##",textstring);
}	

var numDoc=grDG.number.slice(3,10);
var newDocAttachment = new GlideSysAttachment();
var newDocAttachment_sys_id= newDocAttachment.write(grDG,SGQprefCodi+numDoc+".rtf","text/rtf",rtf1.escape().getContent());
grDG.fitxer=newDocAttachment_sys_id;
grDG.sgq=SGQpref;
grDG.nom=current.short_description+" "+SGQprefCodi+" "+grDG.sys_created_on.getDisplayValue();
grDG.tipus="Seguiment";
grDG.plantilla=current.sys_id;
grDG.update();
gs.addInfoMessage("Generat nou document "+grDG.number+" "+grDG.nom);
action.setRedirectURL(current);  //Roman
