sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var xml = new x_udg_q.Xml('<?xml version="1.0" encoding="UTF-8"?>');
    var queryParams = request.queryParams;

    if (!queryParams.centre) return response.setError(new sn_ws_err.NotFoundError('Falta codi del centre'));
    new log('/api/x_udg_q/sgq/processoscentre ' + queryParams.centre);
    var grSgq = new GlideRecord('x_udg_q_sgq');
    grSgq.addQuery('qid', queryParams.centre);
    grSgq.addActiveQuery();
    grSgq.query();
    grSgq.next();
    xml.addTag('centre')
        .addTag('nom', grSgq.nom)
        .addTag('codi', grSgq.codi)
        .addTag('propietari', grSgq.propietari.nom)
        .addTag('responsable', grSgq.responsable.nom);

    var grProces = new GlideRecord('x_udg_q_proces');
    grProces.addQuery('sgq.qid', queryParams.centre);
    grProces.addActiveQuery();
    grProces.orderBy('num');
    grProces.query();
    while (grProces.next()) {
        xml.addTag('proces')
            .addTag('nom', grProces.nom)
            .addTag('titol', grProces.titol)
            .addTag('num', grProces.num)
            .addTag('propietari', grProces.propietari.nom)
            .addTag('responsable', grProces.responsable.nom)
            .addTag('fitxerdocument', grProces.fitxerdocument)
            .addTag('URLfitxer', 'https://udgdev.service-now.com/sys_attachment.do?sys_id=' + grProces.fitxerdocument)
            .addTag('/proces');
    }
    xml.addTag('/centre');

    var str = GlideXMLUtil.removeInvalidChars(xml.toString()); //Ã©s necessari passar per una variable string abans d'escriure el resultat
    response.setContentType('application/xml');
    response.setStatus(200);
    var writer = response.getStreamWriter();
    writer.writeString(str);

})(request, response);
