sysevent_script_action.script:
var username = event.parm1;

var currentUser = gs.getUser();
var gr= new GlideRecord('sys_user');
gr.addQuery ('user_name', username );
gr.query();
if (gr.next()) {
	//new log("Nou Login: "+username);
	var u = new Usuari(gr.sys_id);
	var sgqVisibles= u.sgqVisibles();
	if (sgqVisibles!="") {
		new log("Login: "+username + " és usuari de l'aplicació en els sgq: "+sgqVisibles);
		if (gr.x_udg_q_sgq_visibles!=sgqVisibles) {
			gr.x_udg_q_sgq_visibles=sgqVisibles;
			gr.update();
		}
		if (!gr.x_udg_q_sgq_visibles.includes(gr.x_udg_q_sgq_actual)) {
			gr.x_udg_q_sgq_actual=gr.x_udg_q_sgq_visibles.split(",")[0]; //Agafem el primer de la llista
			gr.update();
		}
		if (gr.x_udg_q_sgq_actual!="") {
			currentUser.savePreference('qualitat.sgqActual',gr.x_udg_q_sgq_actual);
			currentUser.savePreference('qualitat.sgqActualCodi',gr.x_udg_q_sgq_actual.codi.toString());
			u.setAllRoles(gr.x_udg_q_sgq_actual);
		}
	}
	else {
		//new log("Login: "+username+ "No és usuari de l'aplicació");
	}
}
else {
	// new log("ERRROR Usuari logat no trobat: "+username);
}
